import React from 'react';
import './Header.css';
import logo from '../../images/logo_top.png'
import { Route, useHistory, Routes, Redirect, Link  } from 'react-router-dom';
import Navigation from '../Navigation/Navigation';

function Header() {
    return (
        <Routes>
          <Route exact path="/">
            <header className="header header_colored">
              <div className="header__wrap">
                <Link to="/">
                  <img src={logo} alt="Логотип сайта" />
                </Link>
                <Navigation isLoggedIn={isLoggedIn} isHeaderColored={true} />
              </div>
            </header>
          </Route>
          <Route path="/(movies|saved-movies|profile)">
            <header className="header">
              <div className="header__wrap">
                <Link to="/">
                  <img src={logo} alt="Логотип сайта" />
                </Link>
                <Navigation isLoggedIn={isLoggedIn} isHeaderColored={false} />
              </div>
            </header>
          </Route>
        </Routes>
      );
  }
  export default Header;
